<?php

namespace Tests\Feature\Admin;

use App\Repositories\Interfaces\AdminRepositoryInterface;
use App\Repositories\Interfaces\RecordRepositoryInterface;
use App\Repositories\Interfaces\RecordTypeRepositoryInterface;
use App\Repositories\Interfaces\UserRepositoryInterface;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Tests\TestCase;

use Illuminate\Foundation\Testing\DatabaseTransactions;

class RecordsCrudTest extends TestCase
{
    use RefreshDatabase;
    // use DatabaseTransactions;
    protected $seed = true;
    private $adminRepository;
    private $recordRepository;
    private $recordTypeRepository;
    private $userRepository;
    private $sampleData;
    private $admin;
    protected function setUp(): void
    {
        parent::setUp();
        $this->adminRepository = $this->app->make(AdminRepositoryInterface::class);
        $this->recordRepository = $this->app->make(RecordRepositoryInterface::class);
        $this->recordTypeRepository = $this->app->make(RecordTypeRepositoryInterface::class);
        $this->userRepository = $this->app->make(UserRepositoryInterface::class);
        $user = $this->userRepository->all(true)->first();
        $record_type = $this->recordTypeRepository->all(true)->where(['account_type' => $user->account_type])->first();

        $this->sampleData = [
            'text_representation' => "holla",
            'file_path' => "data:audio/mpeg;base64,",
            'record_type_id' => $record_type->id,
            'user_id' => $user->id
        ];
        $this->admin = $this->adminRepository->getBy('name', 'admin');
        $this->refreshApplicationWithLocale('en');
        $this->session(['admin_otp_valid' => encrypt($this->admin->google2fa_secret)]);
    }
    /**
     * A basic feature test example.
     *
     * @return void
     */
    public function test_records_crud_accessible()
    {
        $response = $this->actingAs($this->admin, 'admin')->get(route('admin.records.index'));
        $response->assertStatus(200);
    }
    public function test_records_create_record_accessible()
    {
        $response = $this->actingAs($this->admin, 'admin')->get(route('admin.records.create'));
        $response->assertStatus(200);
    }
    public function test_records_create_record()
    {
        // Working example
        $response = $this->actingAs($this->admin, 'admin')->post(route('admin.records.store'), $this->sampleData);
        $response->assertStatus(302);
        $response->assertSessionHas('success');
        $response = $this->followRedirects($response);
        $response->assertSee(__('admin.success_add', ['thing' => __('global.record')]));
        $record = $this->recordRepository->all()->reverse()->first();
        $this->assertEquals($record->record_type_id, $this->sampleData['record_type_id']);
        $this->assertEquals($record->user_id, $this->sampleData['user_id']);
        $this->assertEquals($record->text_representation, $this->sampleData['text_representation']);
        // Wrong Input -> validation errors
        $response = $this->actingAs($this->admin, 'admin')->post(route('admin.records.store'), []);
        $response->assertStatus(302);
        $response->assertSessionHasErrors(['file_path', 'text_representation', 'record_type_id', 'user_id']);
        // unique user + record type
        $response = $this->actingAs($this->admin, 'admin')->post(route('admin.records.store'), $this->sampleData);
        $response->assertStatus(302);
        $response->assertSessionHasErrors(['record_type_id']);
        // matching record_type and user account_type i.e a personal account's user cannot add a corporate record type
        $record_type = $this->recordTypeRepository->all(true)->where('account_type', '!=', $record->user->account_type)->first();
        $this->sampleData['record_type_id'] = $record_type->id;
        $response = $this->actingAs($this->admin, 'admin')->post(route('admin.records.store'), $this->sampleData);
        $response->assertStatus(302);
        $response->assertSessionHasErrors(['record_type_id']);
    }
    public function test_records_edit_record_accessible()
    {
        $record = $this->recordRepository->create($this->sampleData);
        $response = $this->actingAs($this->admin, 'admin')->get(route('admin.records.edit', ['record' => $record->id]));
        $response->assertStatus(200);
    }
    public function test_records_update_record()
    {
        $record = $this->recordRepository->create($this->sampleData);
        // Working example
        $response = $this->actingAs($this->admin, 'admin')->patch(route('admin.records.update', ['record' => $record->id]), $this->sampleData);
        $response->assertStatus(302);
        $response->assertSessionHas('success');
        $response = $this->followRedirects($response);
        $response->assertSee(__('admin.success_edit', ['thing' => __('global.record')]));
        $record->refresh();
        $this->assertEquals($record->text_representation, $this->sampleData['text_representation']);
        $this->assertEquals($record->user_id, $this->sampleData['user_id']);
        $this->assertEquals($record->record_type_id, $this->sampleData['record_type_id']);
        // Wrong Input -> validation errors
        $response = $this->actingAs($this->admin, 'admin')->patch(route('admin.records.update', ['record' => $record->id]), []);
        $response->assertStatus(302);
        $response->assertSessionHasErrors(['text_representation', 'user_id', 'record_type_id']);
        // matching record_type and user account_type i.e a personal account's user cannot add a corporate record type
        $record_type = $this->recordTypeRepository->all(true)->where('account_type', '!=', $record->user->account_type)->first();
        $this->sampleData['record_type_id'] = $record_type->id;
        $response = $this->actingAs($this->admin, 'admin')->patch(route('admin.records.update', ['record' => $record->id]), $this->sampleData);
        $response->assertStatus(302);
        $response->assertSessionHasErrors(['record_type_id']);
        // unique user + record type
        $user = $this->userRepository->all(true)->where('id', '!=', $record->user->id)->first();
        $record_type = $this->recordTypeRepository->all(true)->where(['account_type' => $user->account_type])->first();
        $this->sampleData['record_type_id'] = $record_type->id;
        $this->sampleData['user_id'] = $user->id;
        $this->recordRepository->create($this->sampleData);
        $response = $this->actingAs($this->admin, 'admin')->patch(route('admin.records.update', ['record' => $record->id]), $this->sampleData);
        $response->assertStatus(302);
        $response->assertSessionHasErrors(['record_type_id']);
    }
    public function test_records_delete_record()
    {
        // Working example
        $record = $this->recordRepository->create($this->sampleData);
        $response = $this->actingAs($this->admin, 'admin')->delete(route('admin.records.destroy', ['record' => $record->id]));
        $response->assertStatus(302);
        $response->assertSessionHas('success');
        $response = $this->followRedirects($response);
        $response->assertSee(__('admin.success_delete', ['thing' => __('global.record')]));
        $this->assertNull($record->fresh());
    }
    public function test_records_batch_delete_records()
    {
        $record = $this->recordRepository->create($this->sampleData);
        $user = $this->userRepository->all(true)->where('id', '!=', $record->user->id)->first();
        $record_type = $this->recordTypeRepository->all(true)->where(['account_type' => $user->account_type])->first();
        $this->sampleData['record_type_id'] = $record_type->id;
        $this->sampleData['user_id'] = $user->id;
        $record1 = $this->recordRepository->create($this->sampleData);
        $response = $this->actingAs($this->admin, 'admin')->delete(route('admin.records.batch_destroy'), [
            'bulk_delete' => json_encode([$record->id, $record1->id])
        ]);
        $response->assertStatus(302);
        $response->assertSessionHas('success');
        $response = $this->followRedirects($response);
        $response->assertSee(__('admin.success_delete', ['thing' => __('global.record')]));
        $this->assertNull($record->fresh());
        $this->assertNull($record1->fresh());
    }
}
